t.script:
document.addEventListener('DOMContentLoaded', function() {
var residencyType = document.getElementById('residency_type');
var nationalId = document.getElementById('national_id');

function updatePlaceholder() {
var placeholderMap = {
'citizen': 'رقم السجل المدني',
'resident': 'رقم الإقامة',
'visitor': 'رقم التصريح',
'other': 'رقم جواز السفر'
};
nationalId.placeholder = placeholderMap[residencyType.value] || '';
}

residencyType.addEventListener('change', updatePlaceholder);
updatePlaceholder(); // Initialize placeholder on page load
});
